mapper.select:
SELECT DISTINCT tb.id AS tagId, tb.`name` AS tagName, tb.industry, tb.rule, tb.business, tt.sche_time AS scheTime, tb.`level`, tb.pid, tb.ctime, tb.utime, tb.state, tb.remark FROM ( SELECT bg.* FROM tbl_user_tag_map utm INNER JOIN tbl_user u ON (utm.`user_id` = u.id) INNER JOIN tbl_basic_tag bg ON (bg.`id` = utm.`tag_id`)
WHERE u.id = #{loginUser.id}
GROUP BY bg.id UNION ALL SELECT bt.* FROM tbl_role_data_map rdm INNER JOIN tbl_basic_tag bt ON (bt.id = rdm.tag_id) WHERE rdm.tag_type = 1
AND rdm.role_id = #{roleBean.id}
GROUP BY bt.id ) tb LEFT JOIN tbl_model tt ON tb.id = tt.tag_id WHERE tb.state != - 1 AND tb.pid = #{bean.tagId}
AND tb.`name` LIKE CONCAT('%',#{bean.tagName},'%')
