mapper.select:
SELECT tb.id, tb.`name`, tb.intro, tb.purpose, tb.state, tb.ctime, tb.utime, tb.remark FROM ( SELECT bg.* FROM
tbl_merge_tag bg
tbl_user_tag_map utm INNER JOIN tbl_user u ON (utm.`user_id` = u.id) INNER JOIN tbl_merge_tag bg ON (bg.`id` = utm.`tag_id`) WHERE u.id = #{loginUser.id}
GROUP BY bg.id UNION ALL SELECT bt.* FROM
tbl_merge_tag bt
tbl_role_data_map rdm INNER JOIN tbl_merge_tag bt ON (bt.id = rdm.tag_id) WHERE rdm.tag_type = 1 AND rdm.role_id = #{roleBean.id}
)tb WHERE 1 = 1 AND tb.state != - 1
AND tb.name = #{bean.name}
GROUP BY tb.id

SELECT DISTINCT tb1.id, tb1.`name`, tb1.`condition`, tb1.intro, tb1.purpose, tb1.state, tb1.ctime, tb1.utime, tb1.remark FROM ( SELECT * FROM tbl_merge_tag WHERE 1=1 AND state != 6
AND name LIKE CONCAT('%',#{mbean.name},'%')
) tb1 INNER JOIN ( SELECT * FROM tbl_user_merge_tag_map ) tb2 ON tb1.id = tb2.merge_tag_id INNER JOIN tbl_merge_tag_tag_map tb3 ON tb1.id = tb3.merge_tag_id INNER JOIN tbl_basic_tag tb4 ON tb3.basic_tag_id = tb4.id ORDER BY tb1.id
LIMIT #{bean.sr,jdbcType=BIGINT},#{bean.er,jdbcType=BIGINT}
